plt.title("Rust vs Python Values")
plt.legend()
plt.grid(True)

plt.subplot(2, 1, 2)
plt.plot(diffs, label="Differences", color='red', marker='.')
plt.title("Differences")
plt.legend()
plt.grid(True)

plt.tight_layout()
plt.show()